import{_ as a,o as s,c as t,ai as i}from"./chunks/framework.ChxbfPAv.js";const u=JSON.parse('{"title":"idea 过代理抓包","description":"","frontmatter":{"bookHidden":true},"headers":[],"relativePath":"article/2025/2025-10-29-idea-proxy.md","filePath":"article/2025/2025-10-29-idea-proxy.md"}'),n={name:"article/2025/2025-10-29-idea-proxy.md"};function r(l,e,o,p,c,d){return s(),t("div",null,[...e[0]||(e[0]=[i(`<h1 id="idea-过代理抓包" tabindex="-1">idea 过代理抓包 <a class="header-anchor" href="#idea-过代理抓包" aria-label="Permalink to “idea 过代理抓包”">​</a></h1><blockquote><p>最近在研究 spring AI，对框架如何调用模型的接口构造参数比较感兴趣，就想在运行程序的时候抓包看一下调用接口的参数。在 IntelliJ Idea 中运行的程序需要单独配置代理（我用的抓包工具是 Charles），这篇文章把我配置的笔记整理一下</p></blockquote><p>在 charles 中导出证书文件 .cer 或者 .pem 拓展名</p><p>然后执行如下命令，将证书导入到 java 的证书中</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>&quot;$JAVA_HOME\\bin\\keytool.exe -import -file $CHARLES_CERTIFICATE_PATH -keystore $JAVA_HOME\\security\\cacerts -alias charles_cert</span></span>
<span class="line"><span># 命令执行会要求输入密码，请记住这个密码 $CHARLES_CERT_PASSWORD</span></span></code></pre></div><p>在 idea 项目的配置中加上如下的 jvm 参数</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>-Djavax.net.ssl.trustStore=&quot;$JAVA_HOME\\security\\cacerts&quot;</span></span>
<span class="line"><span>-Djavax.net.ssl.trustStorePassword=&quot;$CHARLES_CERT_PASSWORD&quot;</span></span>
<span class="line"><span>-DproxySet=true</span></span>
<span class="line"><span>-DproxyHost=127.0.0.1</span></span>
<span class="line"><span>-DproxyPort=8887</span></span></code></pre></div><h2 id="reference" tabindex="-1">reference <a class="header-anchor" href="#reference" aria-label="Permalink to “reference”">​</a></h2><ul><li><a href="https://stackoverflow.com/questions/46926910/how-do-i-hook-up-charles-with-intellij" target="_blank" rel="noreferrer">How do I hook up Charles with IntelliJ?</a></li></ul>`,9)])])}const _=a(n,[["render",r]]);export{u as __pageData,_ as default};
